mapper.update:
update user_record
set username = #{username,jdbcType=VARCHAR},
  r_type = #{rType,jdbcType=CHAR},
  r_date = #{rDate,jdbcType=DATE},
  r_info = #{rInfo,jdbcType=VARCHAR}
where r_id = #{rId,jdbcType=VARCHAR}

mapper.select:
select r_id, username, r_type, r_date, r_info
from user_record
where r_id = #{rId,jdbcType=VARCHAR}
